mapper.select:
SELECT * FROM ( SELECT v.vendor_id AS vendorId, v.company_name AS companyName, ROW_NUMBER() OVER (ORDER BY v.vendor_id DESC) AS rn FROM vendor v WHERE 1=1
AND (v.vendor_id LIKE '%' || #{condition} || '%' OR v.company_name LIKE '%' || #{condition} || '%')
) WHERE rn BETWEEN #{paging.startRow} AND #{paging.endRow}

SELECT * FROM ( SELECT v.vendor_id AS vendorId, v.company_name AS companyName, ROW_NUMBER() OVER (ORDER BY v.company_name) AS rn FROM vendor v WHERE v.type IN (0, 2)
AND (v.company_name LIKE '%' || #{condition} || '%' OR v.vendor_id LIKE '%' || #{condition} || '%')
) WHERE rn BETWEEN #{paging.startRow} AND #{paging.endRow}

SELECT * FROM ( SELECT s.order_id AS orderId, s.order_date AS orderDate, v.company_name AS companyName, ROW_NUMBER() OVER (ORDER BY s.order_date DESC) AS rn FROM sales_order s JOIN vendor v ON s.vendor_id = v.vendor_id WHERE s.status IN ('ÎåÄÍ∏∞', 'Ï≤òÎ¶¨Ï§ë')
AND (s.order_id LIKE '%' || #{condition} || '%' OR v.company_name LIKE '%' || #{condition} || '%')
) WHERE rn BETWEEN #{paging.startRow} AND #{paging.endRow}
