mapper.insert:
SELECT next_date_code('SO') FROM dual
INSERT INTO SALES_ORDER ( order_id, order_date, total_price, vendor_id, status, delivery_date, pay_status, paydue_date
) VALUES ( #{orderId}, SYSDATE, NVL(#{totalPrice}, 0), #{vendorId}, NVL(#{status}, 'ORDER'), #{deliveryDate}, NVL(#{payStatus}, 'UNPAID'), #{paydueDate}
)

mapper.select:
SELECT * FROM ( SELECT p.PROD_ID, p.PROD_NAME, p.SPEC, p.UNIT, p.PROD_UNIT_PRICE, ROWNUM rn FROM ( SELECT PROD_ID, PROD_NAME, SPEC, UNIT, PROD_UNIT_PRICE FROM PRODUCT
AND PROD_NAME LIKE '%' || #{prodName} || '%'
AND STATUS = #{status}
ORDER BY PROD_ID ) p WHERE ROWNUM <= #{offset} + #{pageSize} ) WHERE rn > #{offset}
