mapper.select:
SELECT sale_id AS saleId, sale_total_amount AS saleTotalAmount, TO_CHAR(sale_date, 'YYYY-MM-DD HH24:MI') AS saleDate, sale_pay_type AS salePayType, vendor_id AS vendorId FROM sales_master
vendor_id = #{vendorId}
ORDER BY sale_date DESC

SELECT NVL(SUM(sale_total_amount), 0)
FROM sales_master
WHERE vendor_id = #{vendorId}
  AND TO_CHAR(sale_date, 'YYYY') = #{year}
  AND TO_CHAR(sale_date, 'MM') = LPAD(#{month}, 2, '0')

SELECT COUNT(DISTINCT TO_CHAR(sale_date, 'YYYY-MM-DD'))
FROM sales_master
WHERE vendor_id = #{vendorId}
  AND TO_CHAR(sale_date, 'YYYY') = #{year}
  AND TO_CHAR(sale_date, 'MM') = LPAD(#{month}, 2, '0')
